import Foundation

extension String {
    func subString(from: Int, to: Int) -> String {
       let startIndex = self.index(self.startIndex, offsetBy: from)
       let endIndex = self.index(self.startIndex, offsetBy: to)
       return String(self[startIndex..<endIndex])
    }
}

class Day6 {
    
    static let packageSize = 14 // Change to 4 for Part 1 and 14 for Part 2
    
    class func getNumberOfCharacters() {
        for idx in 0..<input.count - packageSize + 1 {
            let subString = input.subString(from: idx, to: idx + packageSize)
            let setOfCharacters = Set(Array(subString))
            if setOfCharacters.count == subString.count {
                print(subString)
                print(idx + packageSize)
                break
            }
        }
    }
    
    static let exampleInput = """
    mjqjpqmgbljsphdztnvjfqwrcgsmlb
    """
    
    static let input = """

"""
}
